import React, { useState, useEffect } from 'react';
import axios from 'axios';
import QRCode from 'qrcode.react'; // Import QRCode component

const WhatsappSender: React.FC = () => {
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [phoneNumbers, setPhoneNumbers] = useState<string>('');
  const [senderNumber, setSenderNumber] = useState<string>('');
  const [qrCode, setQrCode] = useState<string | null>(null);
  const [senderStatus, setSenderStatus] = useState<string>('not_initialized');

  const initializeSender = async () => {
    if (!senderNumber) {
      alert('Please enter a sender number.');
      return;
    }
    setSenderStatus('initializing');
    try {
      const response = await axios.post('http://localhost:8000/init-sender', { senderNumber });
      if (response.data.qrCode) {
        setQrCode(response.data.qrCode);
        setSenderStatus('qr_received');
      } else if (response.data.status === 'ready') {
        setSenderStatus('ready');
        setQrCode(null);
      } else {
        setSenderStatus('initializing');
        setQrCode(null);
      }
      alert(response.data.message);
    } catch (error) {
      console.error('Error initializing sender:', error);
      setSenderStatus('error');
      setQrCode(null);
      alert('Failed to initialize sender. Check console for details.');
    }
  };

  const checkSenderStatus = async () => {
    if (!senderNumber || senderStatus === 'ready') return;
    try {
      const response = await axios.get(`http://localhost:8000/sender-status/${senderNumber}`);
      if (response.data.status === 'ready') {
        setSenderStatus('ready');
        setQrCode(null);
      } else if (response.data.status === 'qr_received') {
        setQrCode(response.data.qrCode);
        setSenderStatus('qr_received');
      } else {
        setSenderStatus(response.data.status);
      }
    } catch (error) {
      console.error('Error checking sender status:', error);
      setSenderStatus('error');
    }
  };

  useEffect(() => {
    let interval: any;
    if (senderNumber && senderStatus !== 'ready') {
      interval = setInterval(checkSenderStatus, 5000); // Check status every 5 seconds
    }
    return () => clearInterval(interval);
  }, [senderNumber, senderStatus]);

  const handleSenderNumberChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSenderNumber(e.target.value);
    setQrCode(null);
    setSenderStatus('not_initialized');
  };

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    if (event.target.files && event.target.files[0]) {
      setSelectedFile(event.target.files[0]);
    }
  };

  const handleNumbersChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
    setPhoneNumbers(event.target.value);
  };

  const handleSendPdf = async () => {
    if (!senderNumber) {
      alert('Please specify a sender number.');
      return;
    }
    if (senderStatus !== 'ready') {
      alert('Sender is not ready. Please initialize and scan QR code if needed.');
      return;
    }
    if (!selectedFile) {
      alert('Please select a PDF file.');
      return;
    }
    if (!phoneNumbers.trim()) {
      alert('Please enter phone numbers.');
      return;
    }

    const numbersArray = phoneNumbers.split('\n').map(num => num.trim()).filter(num => num);

    const reader = new FileReader();
    reader.readAsDataURL(selectedFile);
    reader.onload = async () => {
      const base64pdf = (reader.result as string).split(',')[1]; // Get base64 part
      const filename = selectedFile.name;

      try {
        await axios.post('http://localhost:8000/send-pdf', {
          senderNumber,
          numbers: numbersArray,
          base64pdf,
          filename,
        });
        alert('PDF sent successfully!');
        setSelectedFile(null);
        setPhoneNumbers('');
      } catch (error) {
        console.error('Error sending PDF:', error);
        alert('Failed to send PDF. Check console for details.');
      }
    };
    reader.onerror = (error) => {
      console.error('Error reading file:', error);
      alert('Error reading file.');
    };
  };

  return (
    <div className="container mt-5">
      <h1>Send WhatsApp PDF</h1>

      <div className="mb-3">
        <label htmlFor="senderNumber" className="form-label">Sender WhatsApp Number (e.g., 6281234567890)</label>
        <input
          type="text"
          className="form-control"
          id="senderNumber"
          value={senderNumber}
          onChange={handleSenderNumberChange}
          placeholder="Enter sender number"
        />
      </div>
      <button className="btn btn-primary mb-3" onClick={initializeSender} disabled={senderStatus === 'initializing' || senderStatus === 'ready'}>
        {senderStatus === 'initializing' ? 'Initializing...' : senderStatus === 'ready' ? 'Ready' : 'Initialize Sender'}
      </button>

      {qrCode && senderStatus === 'qr_received' && (
        <div className="mb-3 text-center">
          <p>Scan this QR code with your WhatsApp app:</p>
          <QRCode value={qrCode} size={256} level="H" includeMargin={true} />
          <p className="mt-2">QR Code will disappear once connected.</p>
        </div>
      )}

      {senderStatus === 'ready' && (
        <div className="alert alert-success mt-3">
          Sender {senderNumber} is ready to send messages!
        </div>
      )}

      {senderStatus === 'error' && (
        <div className="alert alert-danger mt-3">
          Error with sender {senderNumber}. Check console.
        </div>
      )}

      <hr className="my-4" />
      <div className="mb-3">
        <label htmlFor="pdfFile" className="form-label">Upload PDF File</label>
        <input type="file" className="form-control" id="pdfFile" accept=".pdf" onChange={handleFileChange} />
      </div>
      <div className="mb-3">
        <label htmlFor="phoneNumbers" className="form-label">Phone Numbers (one per line, e.g., 6281234567890)</label>
        <textarea className="form-control" id="phoneNumbers" rows={5} value={phoneNumbers} onChange={handleNumbersChange}></textarea>
      </div>
      <button className="btn btn-primary mb-3" onClick={handleSendPdf}>Send PDF via WhatsApp</button>
    </div>
  );
};

export default WhatsappSender;
