import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface Record {
  id: number;
  staffId: number;
  timestamp: string;
  type: 'clock-in' | 'clock-out';
}

const ClockInOut = () => {
  const [records, setRecords] = useState<Record[]>([]);
  const [status, setStatus] = useState('out');
  const [error, setError] = useState('');

  const user = JSON.parse(localStorage.getItem('user') || '{}');
  const staffId = user.staffId || null;

  const fetchRecords = async (id: number) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/records/${id}`);
      const today = new Date().toISOString().slice(0, 10);
      const todaysRecords = (response.data as Record[]).filter((r: Record) => r.timestamp.startsWith(today));
      setRecords(todaysRecords);

      if (todaysRecords.length > 0) {
        setStatus(todaysRecords[todaysRecords.length - 1].type === 'clock-in' ? 'in' : 'out');
      } else {
        setStatus('out'); // Reset status if no records for today
      }
    } catch (error) {
      console.error('Error fetching records:', error);
      setStatus('out'); // Reset status on error
    }
  };

  useEffect(() => {
    if (staffId !== null) {
      fetchRecords(staffId);
    }
  }, [staffId]);

  const handleClockIn = async () => {
    try {
      await axios.post('http://localhost:5000/api/records', { staffId, type: 'clock-in' });
      fetchRecords(staffId);
      setError('');
    } catch (error: any) {
      setError(error.response.data.message);
    }
  };

  const handleClockOut = async () => {
    try {
      await axios.post('http://localhost:5000/api/records', { staffId, type: 'clock-out' });
      fetchRecords(staffId);
      setError('');
    } catch (error: any) {
      setError(error.response.data.message);
    }
  };

  return (
    <div className="clock-in-out-container">
      <div className="clock-in-out-box">
        <h2>Clock In/Out</h2>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <p className="status-message">Status: Anda sedang {status === 'in' ? 'bekerja' : 'tidak bekerja'}</p>
        <button className="btn btn-primary" onClick={handleClockIn} disabled={status === 'in'}>Clock In</button>
        <button className="btn btn-danger" onClick={handleClockOut} disabled={status === 'out'}>Clock Out</button>
        <h3>Today's Activity</h3>
        <ul>
          {records.map(record => (
            <li key={record.id}>
              {record.type} at {new Date(record.timestamp).toLocaleTimeString()}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ClockInOut;
