import React, { useState } from 'react';
import axios from 'axios';

interface LoginPageProps {
  onLogin: (user: any) => void;
}

const LoginPage: React.FC<LoginPageProps> = ({ onLogin }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState(''); // For registration
  const [error, setError] = useState<string | null>(null);
  const [isRegistering, setIsRegistering] = useState(false); // To toggle between login and register forms
  const [showPassword, setShowPassword] = useState(false); // To toggle password visibility

  const handleLogin = (e: React.FormEvent) => {
    e.preventDefault();
    axios.post('http://localhost:5000/api/login', { username, password })
      .then(response => {
        console.log('Login response:', response);
        if (response.data.success) {
          onLogin(response.data.user);
        } else {
          setError(response.data.message);
        }
      })
      .catch(error => {
        console.error('Login error:', error);
        setError('An error occurred during login.');
      });
  };

  const handleRegister = (e: React.FormEvent) => {
    e.preventDefault();

    if (!name || !username || !password) {
      setError('All fields are required.');
      return;
    }

    axios.post('http://localhost:5000/api/register', { username, password, name })
      .then(response => {
        if (response.data.success) {
          alert('Account created successfully! Please log in.');
          setIsRegistering(false); // Switch back to login form
          setUsername('');
          setPassword('');
          setName('');
          setError(null);
        } else {
          setError(response.data.message);
        }
      })
      .catch(error => {
        console.error('Registration error:', error);
        if (error.response && error.response.data && error.response.data.message) {
          setError(error.response.data.message);
        } else {
          setError('An error occurred during registration.');
        }
      });
  };

  return (
    <div className="container mt-5">
      <h1>{isRegistering ? 'Register' : 'Login'}</h1>
      {error && <div className="alert alert-danger">{error}</div>}

      {!isRegistering ? (
        <form onSubmit={handleLogin}>
          <div className="mb-3">
            <label className="form-label">Username</label>
            <input
              type="text"
              className="form-control"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Password</label>
            <div className="input-group">
              <input
                type={showPassword ? 'text' : 'password'}
                className="form-control"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              <button
                className="btn btn-outline-secondary"
                type="button"
                onClick={() => setShowPassword(!showPassword)}
              >
                {showPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è'}
              </button>
            </div>
          </div>
          <button type="submit" className="btn btn-primary">Login</button>
          <button type="button" className="btn btn-link" onClick={() => setIsRegistering(true)}>Register</button>
        </form>
      ) : (
        <form onSubmit={handleRegister}>
          <div className="mb-3">
            <label className="form-label">Name</label>
            <input
              type="text"
              className="form-control"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Username</label>
            <input
              type="text"
              className="form-control"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Password</label>
            <div className="input-group">
              <input
                type={showPassword ? 'text' : 'password'}
                className="form-control"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              <button
                className="btn btn-outline-secondary"
                type="button"
                onClick={() => setShowPassword(!showPassword)}
              >
                {showPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è'}
              </button>
            </div>
          </div>
          <button type="submit" className="btn btn-success">Register</button>
          <button type="button" className="btn btn-link" onClick={() => setIsRegistering(false)}>Back to Login</button>
        </form>
      )}
    </div>
  );
};

export default LoginPage;
