import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

// Define the office location (latitude and longitude)
const OFFICE_LOCATION = {
  latitude: -6.958115592601739, // Replace with your office's latitude
  longitude: 107.70755749004297, // Replace with your office's longitude
};
const GEOFENCE_RADIUS = 1; // in meters

interface Staff {
  id: number;
  name: string;
}

interface Attendance {
  id: number;
  staffId: number;
  name: string;
  status: string;
  date: string;
}

function App() {
  const [staff, setStaff] = useState<Staff[]>([]);
  const [attendance, setAttendance] = useState<Attendance[]>([]);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    // Fetch staff and attendance data from the backend
    axios.get('http://localhost:5000/api/staff').then((response) => setStaff(response.data));
    axios.get('http://localhost:5000/api/attendance').then((response) => setAttendance(response.data));
  }, []);

  const handleAttendance = (staffId: number, name: string, status: string) => {
    const date = new Date().toISOString().split('T')[0];

    if (status === 'Hadir') {
      if (!navigator.geolocation) {
        setError('Geolocation is not supported by your browser');
        return;
      }

      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          const distance = getDistance(latitude, longitude, OFFICE_LOCATION.latitude, OFFICE_LOCATION.longitude);

          if (distance <= GEOFENCE_RADIUS) {
            submitAttendance(staffId, name, status, date);
          } else {
            setError('You are not within the office radius to mark attendance.');
          }
        },
        () => {
          setError('Unable to retrieve your location.');
        }
      );
    } else {
      submitAttendance(staffId, name, status, date);
    }
  };

  const submitAttendance = (staffId: number, name: string, status: string, date: string) => {
    axios.post('http://localhost:5000/api/attendance', { staffId, name, status, date }).then((response) => {
      setAttendance([...attendance, response.data]);
      setError(null);
    });
  };

  const getDistance = (lat1: number, lon1: number, lat2: number, lon2: number) => {
    const R = 6371e3; // metres
    const φ1 = (lat1 * Math.PI) / 180; // φ, λ in radians
    const φ2 = (lat2 * Math.PI) / 180;
    const Δφ = ((lat2 - lat1) * Math.PI) / 180;
    const Δλ = ((lon2 - lon1) * Math.PI) / 180;

    const a = Math.sin(Δφ / 2) * Math.sin(Δφ / 2) +
              Math.cos(φ1) * Math.cos(φ2) *
              Math.sin(Δλ / 2) * Math.sin(Δλ / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));

    return R * c; // in metres
  };

  return (
    <div className="container mt-5">
      <h1 className="mb-4">Aplikasi Absensi Staff</h1>

      {error && <div className="alert alert-danger">{error}</div>}

      <div className="row">
        {staff.map((s) => (
          <div key={s.id} className="col-md-4 mb-4">
            <div className="card">
              <div className="card-body">
                <h5 className="card-title">{s.name}</h5>
                <button className="btn btn-success me-2" onClick={() => handleAttendance(s.id, s.name, 'Hadir')}>
                  Hadir
                </button>
                <button className="btn btn-warning me-2" onClick={() => handleAttendance(s.id, s.name, 'Sakit')}>
                  Sakit
                </button>
                <button className="btn btn-info" onClick={() => handleAttendance(s.id, s.name, 'Izin')}>
                  Izin
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      <h2 className="mt-5">Riwayat Absensi</h2>
      <table className="table">
        <thead>
          <tr>
            <th>Nama</th>
            <th>Status</th>
            <th>Tanggal</th>
          </tr>
        </thead>
        <tbody>
          {attendance.map((a) => (
            <tr key={a.id}>
              <td>{a.name}</td>
              <td>{a.status}</td>
              <td>{a.date}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
