import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

interface Attendance {
  id: number;
  staffId: number;
  name: string;
  status: string;
  date: string;
  time: string; // Added time property
}

const AdminDashboard: React.FC = () => {
  const [attendance, setAttendance] = useState<Attendance[]>([]);
  const [sortColumn, setSortColumn] = useState<keyof Attendance | null>(null);
  const [sortDirection, setSortDirection] = useState<'asc' | 'desc'>('asc');
  const [searchTerm, setSearchTerm] = useState<string>('');

  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [phoneNumbers, setPhoneNumbers] = useState<string>('');

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    if (event.target.files && event.target.files[0]) {
      setSelectedFile(event.target.files[0]);
    }
  };

  const handleNumbersChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
    setPhoneNumbers(event.target.value);
  };

  const handleSendPdf = async () => {
    if (!selectedFile) {
      alert('Please select a PDF file.');
      return;
    }
    if (!phoneNumbers.trim()) {
      alert('Please enter phone numbers.');
      return;
    }

    const numbersArray = phoneNumbers.split('\n').map(num => num.trim()).filter(num => num);

    const reader = new FileReader();
    reader.readAsDataURL(selectedFile);
    reader.onload = async () => {
      const base64pdf = (reader.result as string).split(',')[1]; // Get base64 part
      const filename = selectedFile.name;

      try {
        await axios.post('http://localhost:8000/send-pdf', {
          numbers: numbersArray,
          base64pdf,
          filename,
        });
        alert('PDF sent successfully!');
        setSelectedFile(null);
        setPhoneNumbers('');
      } catch (error) {
        console.error('Error sending PDF:', error);
        alert('Failed to send PDF. Check console for details.');
      }
    };
    reader.onerror = (error) => {
      console.error('Error reading file:', error);
      alert('Error reading file.');
    };
  };

  const handleExport = () => {
    window.open('http://localhost:5000/api/export-excel', '_blank');
  };

  useEffect(() => {
    axios.get('http://localhost:5000/api/attendance').then((response) => {
      setAttendance(response.data);
    });
  }, []);

  const handleSort = (column: keyof Attendance) => {
    if (sortColumn === column) {
      setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc');
    } else {
      setSortColumn(column);
      setSortDirection('asc');
    }
  };

  const sortedAttendance = [...attendance]
    .filter((a) =>
      a.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      a.status.toLowerCase().includes(searchTerm.toLowerCase()) ||
      a.date.toLowerCase().includes(searchTerm.toLowerCase()) ||
      a.time.toLowerCase().includes(searchTerm.toLowerCase())
    )
    .sort((a, b) => {
    if (sortColumn === null) return 0;

    const aValue = a[sortColumn];
    const bValue = b[sortColumn];

    // Custom sorting for time if the column is 'time'
    if (sortColumn === 'time') {
      const timeA = new Date(`2000/01/01 ${a.time}`); // Use a dummy date for time comparison
      const timeB = new Date(`2000/01/01 ${b.time}`);
      return sortDirection === 'asc' ? timeA.getTime() - timeB.getTime() : timeB.getTime() - timeA.getTime();
    }

    if (typeof aValue === 'string' && typeof bValue === 'string') {
      return sortDirection === 'asc' ? aValue.localeCompare(bValue) : bValue.localeCompare(aValue);
    } else if (typeof aValue === 'number' && typeof bValue === 'number') {
      return sortDirection === 'asc' ? aValue - bValue : bValue - aValue;
    }
    return 0;
  });

  return (
    <div className="container mt-5">
      <h1>Admin Dashboard</h1>
      <button className="btn btn-success mb-3" onClick={handleExport}>Export to Excel</button>
      <Link to="/whatsapp-sender" className="btn btn-info mb-3 ms-2">Go to WhatsApp Sender</Link>

      <h2 className="mt-5">All Attendance Records</h2>
      <div className="mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Cari riwayat absensi..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>
      <table className="table">
        <thead>
          <tr>
            <th onClick={() => handleSort('name')} style={{ cursor: 'pointer' }}>
              Name {sortColumn === 'name' && (sortDirection === 'asc' ? '▲' : '▼')}
            </th>
            <th onClick={() => handleSort('status')} style={{ cursor: 'pointer' }}>
              Status {sortColumn === 'status' && (sortDirection === 'asc' ? '▲' : '▼')}
            </th>
            <th onClick={() => handleSort('date')} style={{ cursor: 'pointer' }}>
              Date {sortColumn === 'date' && (sortDirection === 'asc' ? '▲' : '▼')}
            </th>
            <th onClick={() => handleSort('time')} style={{ cursor: 'pointer' }}>
              Time {sortColumn === 'time' && (sortDirection === 'asc' ? '▲' : '▼')}
            </th>
          </tr>
        </thead>
        <tbody>
          {sortedAttendance.map((a) => (
            <tr key={a.id}>
              <td>{a.name}</td>
              <td>{a.status}</td>
              <td>{a.date}</td>
              <td>{a.time}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminDashboard;
