import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface AttendanceRecord {
  id: number;
  name: string;
  timestamp: string;
}

const AttendanceLog: React.FC = () => {
  const [log, setLog] = useState<AttendanceRecord[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    axios.get('http://localhost:5000/api/attendance')
      .then(response => {
        if (response.data.success) {
          setLog(response.data.log);
        } else {
          setError(response.data.message);
        }
      })
      .catch(error => {
        console.error('Error fetching attendance log:', error);
        setError('Failed to fetch attendance log.');
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  if (loading) {
    return (
      <div className="card mt-4">
        <div className="card-body text-center">
          <p>Loading attendance log...</p>
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="card mt-4">
        <div className="card-body">
          <div className="alert alert-danger">{error}</div>
        </div>
      </div>
    );
  }

  return (
    <div className="card mt-4">
      <div className="card-header">
        <h5 className="card-title">Log Pencatatan Kehadiran</h5>
      </div>
      <div className="card-body">
        <div className="table-responsive">
          <table className="table table-striped table-hover">
            <thead>
              <tr>
                <th scope="col">#</th>
                <th scope="col">Nama</th>
                <th scope="col">Tanggal</th>
                <th scope="col">Jam Masuk</th>
              </tr>
            </thead>
            <tbody>
              {log.length > 0 ? (
                log.map((record, index) => {
                  const date = new Date(record.timestamp);
                  return (
                    <tr key={record.id}>
                      <th scope="row">{index + 1}</th>
                      <td>{record.name}</td>
                      <td>{date.toLocaleDateString('id-ID')}</td>
                      <td>{date.toLocaleTimeString('id-ID', { hour: '2-digit', minute: '2-digit' })}</td>
                    </tr>
                  );
                })
              ) : (
                <tr>
                  <td colSpan={4} className="text-center">Tidak ada data kehadiran.</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default AttendanceLog;
