import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

interface ClockInOutRecord {
  id: number;
  staffId: number;
  timestamp: string;
  type: 'clock-in' | 'clock-out';
}

interface AttendanceRecord {
  id: number;
  staffId: number;
  name: string;
  status: string;
  date: string;
  time: string;
}

interface Staff {
  id: number;
  name: string;
}

interface AdminDashboardProps {
  onLogout: () => void;
}

const AdminDashboard: React.FC<AdminDashboardProps> = ({ onLogout }) => {
  const [clockInOutRecords, setClockInOutRecords] = useState<ClockInOutRecord[]>([]);
  const [attendanceRecords, setAttendanceRecords] = useState<AttendanceRecord[]>([]);
  const [staff, setStaff] = useState<Staff[]>([]);
  const [selectedStaffId, setSelectedStaffId] = useState<number | null>(null);
  const [sortColumn, setSortColumn] = useState<keyof Record | null>(null);
  const [sortDirection, setSortDirection] = useState<'asc' | 'desc'>('asc');
  const [searchTerm, setSearchTerm] = useState<string>('');
  const navigate = useNavigate();

  const handleExport = () => {
    window.open('http://localhost:5000/api/export-excel', '_blank');
  };

  const handleLogout = () => {
    onLogout();
  };

  useEffect(() => {
    axios.get('http://localhost:5000/api/staff').then((response) => {
      setStaff(response.data);
    });
  }, []);

  useEffect(() => {
    if (selectedStaffId) {
      axios.get(`http://localhost:5000/api/records/${selectedStaffId}`).then((response) => {
        setClockInOutRecords(response.data);
      });
      axios.get(`http://localhost:5000/api/attendance`).then((response) => {
        // Filter attendance records for the selected staffId
        const filteredAttendance = response.data.filter((record: AttendanceRecord) => record.staffId === selectedStaffId);
        setAttendanceRecords(filteredAttendance);
      });
    } else {
      setClockInOutRecords([]);
      setAttendanceRecords([]);
    }
  }, [selectedStaffId]);

  const handleSort = (column: keyof ClockInOutRecord | keyof AttendanceRecord) => {
    if (sortColumn === column) {
      setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc');
    } else {
      setSortColumn(column as keyof ClockInOutRecord);
      setSortDirection('asc');
    }
  };

  const sortedClockInOutRecords = [...clockInOutRecords]
    .filter((a) =>
      a.timestamp.toLowerCase().includes(searchTerm.toLowerCase()) ||
      a.type.toLowerCase().includes(searchTerm.toLowerCase())
    )
    .sort((a, b) => {
      if (sortColumn === null) return 0;

      const aValue = a[sortColumn as keyof ClockInOutRecord];
      const bValue = b[sortColumn as keyof ClockInOutRecord];

      if (sortColumn === 'timestamp') {
        const timeA = new Date(a.timestamp).getTime();
        const timeB = new Date(b.timestamp).getTime();
        return sortDirection === 'asc' ? timeA - timeB : timeB - timeA;
      }

      if (typeof aValue === 'string' && typeof bValue === 'string') {
        return sortDirection === 'asc' ? aValue.localeCompare(bValue) : bValue.localeCompare(aValue);
      }
      return 0;
    });

  const sortedAttendanceRecords = [...attendanceRecords]
    .filter((a) =>
      a.date.toLowerCase().includes(searchTerm.toLowerCase()) ||
      a.status.toLowerCase().includes(searchTerm.toLowerCase())
    )
    .sort((a, b) => {
      if (sortColumn === null) return 0;

      const aValue = a[sortColumn as keyof AttendanceRecord];
      const bValue = b[sortColumn as keyof AttendanceRecord];

      if (sortColumn === 'date') {
        const dateA = new Date(a.date).getTime();
        const dateB = new Date(b.date).getTime();
        return sortDirection === 'asc' ? dateA - dateB : dateB - dateA;
      }

      if (typeof aValue === 'string' && typeof bValue === 'string') {
        return sortDirection === 'asc' ? aValue.localeCompare(bValue) : bValue.localeCompare(aValue);
      }
      return 0;
    });

  return (
    <div className="container mt-5">
      <button className="btn btn-danger mb-3" onClick={handleLogout}>Logout</button>
      <h1>Admin Dashboard</h1>
      <button className="btn btn-success mb-3" onClick={handleExport}>Export to Excel</button>

      <h2 className="mt-5">Attendance Records</h2>
      <div className="mb-3">
        <label htmlFor="staff-select" className="form-label">Select Staff:</label>
        <select
          id="staff-select"
          className="form-select"
          value={selectedStaffId || ''}
          onChange={(e) => setSelectedStaffId(e.target.value ? parseInt(e.target.value) : null)}
        >
          <option value="">-- Select a Staff --</option>
          {staff.map((s) => (
            <option key={s.id} value={s.id}>{s.name}</option>
          ))}
        </select>
      </div>

      {selectedStaffId && (
        <>
          <div className="mb-3 mt-3">
            <input
              type="text"
              className="form-control"
              placeholder="Cari riwayat absensi..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
          <h3 className="mt-4">Clock-in History</h3>
          <table className="table">
            <thead>
              <tr>
                <th onClick={() => handleSort('timestamp')} style={{ cursor: 'pointer' }}>
                  Timestamp {sortColumn === 'timestamp' && (sortDirection === 'asc' ? '▲' : '▼')}
                </th>
              </tr>
            </thead>
            <tbody>
              {sortedAttendance.filter(a => a.type === 'clock-in').map((a) => (
                <tr key={a.id}>
                  <td>{new Date(a.timestamp).toLocaleString()}</td>
                </tr>
              ))}
            </tbody>
          </table>

          <h3 className="mt-4">Clock-out History</h3>
          <table className="table">
            <thead>
              <tr>
                <th onClick={() => handleSort('timestamp')} style={{ cursor: 'pointer' }}>
                  Timestamp {sortColumn === 'timestamp' && (sortDirection === 'asc' ? '▲' : '▼')}
                </th>
              </tr>
            </thead>
            <tbody>
              {sortedAttendance.filter(a => a.type === 'clock-out').map((a) => (
                <tr key={a.id}>
                  <td>{new Date(a.timestamp).toLocaleString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}
      {!selectedStaffId && <p>Please select a staff member to view their attendance records.</p>}
    </div>
  );
};

export default AdminDashboard;
