import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import axios from 'axios';
import './App.css';
import LoginPage from './LoginPage';
import AdminDashboard from './AdminDashboard';
import AuthRedirect from './AuthRedirect';

// Define the office location (latitude and longitude)
const OFFICE_LOCATION = {
  latitude: -6.958115592601739, // Replace with your office's latitude
  longitude: 107.70755749004297, // Replace with your office's longitude
};
const GEOFENCE_RADIUS = 100; // in meters

interface Staff {
  id: number;
  name: string;
}

interface Attendance {
  id: number;
  staffId: number;
  name: string;
  status: string;
  date: string;
}

interface User {
  id: number;
  username: string;
  role: 'admin' | 'staff';
  staffId?: number;
}

function App() {
  const [user, setUser] = useState<User | null>(null);

  const handleLogin = (loggedInUser: User) => {
    setUser(loggedInUser);
  };

  const handleLogout = () => {
    setUser(null);
  };

  return (
    <Router>
      <AuthRedirect user={user} />
      <Routes>
        <Route path="/login" element={<LoginPage onLogin={handleLogin} />} />
        <Route path="/" element={user && user.role === 'staff' ? <MainApp user={user} onLogout={handleLogout} /> : null} />
        <Route path="/admin" element={user && user.role === 'admin' ? <AdminDashboard /> : null} />
      </Routes>
    </Router>
  );
}

interface MainAppProps {
  user: User;
  onLogout: () => void;
}

const MainApp: React.FC<MainAppProps> = ({ user, onLogout }) => {
  const [staff, setStaff] = useState<Staff[]>([]);
  const [attendance, setAttendance] = useState<Attendance[]>([]);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    axios.get('http://localhost:5000/api/staff').then((response) => setStaff(response.data));
    axios.get('http://localhost:5000/api/attendance').then((response) => setAttendance(response.data));
  }, []);

  const handleAttendance = (staffId: number, name: string, status: string) => {
    const date = new Date().toISOString().split('T')[0];

    if (status === 'Hadir') {
      if (!navigator.geolocation) {
        setError('Geolocation is not supported by your browser');
        return;
      }

      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          const distance = getDistance(latitude, longitude, OFFICE_LOCATION.latitude, OFFICE_LOCATION.longitude);

          if (distance <= GEOFENCE_RADIUS) {
            submitAttendance(staffId, name, status, date);
          } else {
            setError('You are not within the office radius to mark attendance.');
          }
        },
        () => {
          setError('Unable to retrieve your location.');
        }
      );
    } else {
      submitAttendance(staffId, name, status, date);
    }
  };

  const submitAttendance = (staffId: number, name: string, status: string, date: string) => {
    axios.post('http://localhost:5000/api/attendance', { staffId, name, status, date })
      .then((response) => {
        setAttendance([...attendance, response.data]);
        setError(null);
      })
      .catch((err) => {
        if (err.response && err.response.status === 400) {
          setError(err.response.data.message);
        } else {
          setError('An error occurred while submitting attendance.');
        }
      });
  };

  const getDistance = (lat1: number, lon1: number, lat2: number, lon2: number) => {
    const R = 6371e3; // metres
    const φ1 = (lat1 * Math.PI) / 180; // φ, λ in radians
    const φ2 = (lat2 * Math.PI) / 180;
    const Δφ = ((lat2 - lat1) * Math.PI) / 180;
    const Δλ = ((lon2 - lon1) * Math.PI) / 180;

    const a = Math.sin(Δφ / 2) * Math.sin(Δφ / 2) +
              Math.cos(φ1) * Math.cos(φ2) *
              Math.sin(Δλ / 2) * Math.sin(Δλ / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));

    return R * c; // in metres
  };

  return (
    <div className="container mt-5">
      <button className="btn btn-danger mb-4" onClick={onLogout}>Logout</button>
      <h1 className="mb-4">Aplikasi Absensi Staff</h1>

      {error && <div className="alert alert-danger">{error}</div>}

      <div className="row">
        {staff
          .filter((s) => user.role === 'admin' || (user.role === 'staff' && s.id === user.staffId))
          .map((s) => (
            <div key={s.id} className="col-md-4 mb-4">
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">{s.name}</h5>
                  <button className="btn btn-success me-2" onClick={() => handleAttendance(s.id, s.name, 'Hadir')}>
                    Hadir
                  </button>
                  <button className="btn btn-warning me-2" onClick={() => handleAttendance(s.id, s.name, 'Sakit')}>
                    Sakit
                  </button>
                  <button className="btn btn-info" onClick={() => handleAttendance(s.id, s.name, 'Izin')}>
                    Izin
                  </button>
                </div>
              </div>
            </div>
          ))}
      </div>

      <h2 className="mt-5">Riwayat Absensi</h2>
      <table className="table">
        <thead>
          <tr>
            <th>Nama</th>
            <th>Status</th>
            <th>Tanggal</th>
          </tr>
        </thead>
        <tbody>
          {attendance
            .filter((a) => user.role === 'admin' || (user.role === 'staff' && a.staffId === user.staffId))
            .map((a) => (
              <tr key={a.id}>
                <td>{a.name}</td>
                <td>{a.status}</td>
                <td>{a.date}</td>
              </tr>
            ))}
        </tbody>
      </table>
    </div>
  );
};

export default App;
