import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ClockInOut = () => {
  const [records, setRecords] = useState([]);
  const [status, setStatus] = useState('out');
  const [error, setError] = useState('');

  const staffId = JSON.parse(localStorage.getItem('user') || '{}').staffId;

  const fetchRecords = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/records/${staffId}`);
      const today = new Date().toISOString().slice(0, 10);
      const todaysRecords = response.data.filter(r => r.timestamp.startsWith(today));
      setRecords(todaysRecords);

      if (todaysRecords.length > 0) {
        setStatus(todaysRecords[todaysRecords.length - 1].type === 'clock-in' ? 'in' : 'out');
      }
    } catch (error) {
      console.error('Error fetching records:', error);
    }
  };

  useEffect(() => {
    if (staffId) {
      fetchRecords();
    }
  }, [staffId]);

  const handleClockIn = async () => {
    try {
      await axios.post('http://localhost:5000/api/records', { staffId, type: 'clock-in' });
      fetchRecords();
      setError('');
    } catch (error) {
      setError(error.response.data.message);
    }
  };

  const handleClockOut = async () => {
    try {
      await axios.post('http://localhost:5000/api/records', { staffId, type: 'clock-out' });
      fetchRecords();
      setError('');
    } catch (error) {
      setError(error.response.data.message);
    }
  };

  return (
    <div>
      <h2>Clock In/Out</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <p>Status: Anda sedang {status === 'in' ? 'bekerja' : 'tidak bekerja'}</p>
      <button onClick={handleClockIn} disabled={status === 'in'}>Clock In</button>
      <button onClick={handleClockOut} disabled={status === 'out'}>Clock Out</button>
      <h3>Today's Activity</h3>
      <ul>
        {records.map(record => (
          <li key={record.id}>
            {record.type} at {new Date(record.timestamp).toLocaleTimeString()}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ClockInOut;
