import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

interface ClockInOutRecord {
  id: number;
  staffId: number;
  timestamp: string;
  type: 'clock-in' | 'clock-out';
}

interface WorkTimeRecord {
  id: number;
  staffId: number;
  name: string;
  date: string;
  clockInTime: string;
  clockOutTime: string;
  totalHours: string;
}

interface AttendanceRecord {
  id: number;
  staffId: number;
  name: string;
  status: string;
  date: string;
  time: string;
}

interface Staff {
  id: number;
  name: string;
}

interface AdminDashboardProps {
  onLogout: () => void;
}

const AdminDashboard: React.FC<AdminDashboardProps> = ({ onLogout }) => {
  const [clockInOutRecords, setClockInOutRecords] = useState<ClockInOutRecord[]>([]);
  const [attendanceRecords, setAttendanceRecords] = useState<AttendanceRecord[]>([]);
  const [staff, setStaff] = useState<Staff[]>([]);
  const [selectedStaffId, setSelectedStaffId] = useState<number | null>(null);
  const [clockInOutSortColumn, setClockInOutSortColumn] = useState<keyof ClockInOutRecord | null>(null);
  const [clockInOutSortDirection, setClockInOutSortDirection] = useState<'asc' | 'desc'>('asc');
  const [clockInOutSearchTerm, setClockInOutSearchTerm] = useState<string>('');

  const [attendanceSortColumn, setAttendanceSortColumn] = useState<keyof AttendanceRecord | null>(null);
  const [attendanceSortDirection, setAttendanceSortDirection] = useState<'asc' | 'desc'>('asc');
  const [attendanceSearchTerm, setAttendanceSearchTerm] = useState<string>('');

  const [workTimeRecords, setWorkTimeRecords] = useState<WorkTimeRecord[]>([]);
  const [workTimeSortColumn, setWorkTimeSortColumn] = useState<keyof WorkTimeRecord | null>(null);
  const [workTimeSortDirection, setWorkTimeSortDirection] = useState<'asc' | 'desc'>('asc');
  const [workTimeSearchTerm, setWorkTimeSearchTerm] = useState<string>('');
  const navigate = useNavigate();

  const handleExport = () => {
    window.open('http://localhost:5000/api/export-excel', '_blank');
  };

  const handleLogout = () => {
    onLogout();
  };

  useEffect(() => {
    axios.get('http://localhost:5000/api/staff').then((response) => {
      setStaff(response.data);
    });
  }, []);

  useEffect(() => {
    if (selectedStaffId) {
      axios.get(`http://localhost:5000/api/records/${selectedStaffId}`).then((response) => {
        setClockInOutRecords(response.data);
      });
      axios.get(`http://localhost:5000/api/attendance`).then((response) => {
        // Filter attendance records for the selected staffId
        const filteredAttendance = response.data.filter((record: AttendanceRecord) => record.staffId === selectedStaffId);
        setAttendanceRecords(filteredAttendance);
      });
    } else {
      setClockInOutRecords([]);
      setAttendanceRecords([]);
    }
  }, [selectedStaffId]);

  const handleClockInOutSort = (column: keyof ClockInOutRecord) => {
    if (clockInOutSortColumn === column) {
      setClockInOutSortDirection(clockInOutSortDirection === 'asc' ? 'desc' : 'asc');
    } else {
      setClockInOutSortColumn(column);
      setClockInOutSortDirection('asc');
    }
  };

  const handleAttendanceSort = (column: keyof AttendanceRecord) => {
    if (attendanceSortColumn === column) {
      setAttendanceSortDirection(attendanceSortDirection === 'asc' ? 'desc' : 'asc');
    } else {
      setAttendanceSortColumn(column);
      setAttendanceSortDirection('asc');
    }
  };

  const sortedClockInOutRecords = [...clockInOutRecords]
    .filter((a) =>
      a.timestamp.toLowerCase().includes(clockInOutSearchTerm.toLowerCase()) ||
      a.type.toLowerCase().includes(clockInOutSearchTerm.toLowerCase())
    )
    .sort((a, b) => {
      if (clockInOutSortColumn === null) return 0;

      const aValue = a[clockInOutSortColumn];
      const bValue = b[clockInOutSortColumn];

      if (clockInOutSortColumn === 'timestamp') {
        const timeA = new Date(a.timestamp).getTime();
        const timeB = new Date(b.timestamp).getTime();
        return clockInOutSortDirection === 'asc' ? timeA - timeB : timeB - timeA;
      }

      if (typeof aValue === 'string' && typeof bValue === 'string') {
        return clockInOutSortDirection === 'asc' ? aValue.localeCompare(bValue) : bValue.localeCompare(aValue);
      }
      return 0;
    });

  const sortedAttendanceRecords = [...attendanceRecords]
    .filter((a) =>
      a.date.toLowerCase().includes(attendanceSearchTerm.toLowerCase()) ||
      a.status.toLowerCase().includes(attendanceSearchTerm.toLowerCase())
    )
    .sort((a, b) => {
      if (attendanceSortColumn === null) return 0;

      const aValue = a[attendanceSortColumn];
      const bValue = b[attendanceSortColumn];

      if (attendanceSortColumn === 'date') {
        const dateA = new Date(a.date).getTime();
        const dateB = new Date(b.date).getTime();
        return attendanceSortDirection === 'asc' ? dateA - dateB : dateB - dateA;
      }

      if (typeof aValue === 'string' && typeof bValue === 'string') {
        return attendanceSortDirection === 'asc' ? aValue.localeCompare(bValue) : bValue.localeCompare(aValue);
      }
      return 0;
    });

  return (
    <div className="container mt-5">
      <button className="btn btn-danger mb-3" onClick={handleLogout}>Logout</button>
      <h1>Admin Dashboard</h1>
      <button className="btn btn-success mb-3" onClick={handleExport}>Export to Excel</button>

      <h2 className="mt-5">Attendance Records</h2>
      <div className="mb-3">
        <label htmlFor="staff-select" className="form-label">Select Staff:</label>
        <select
          id="staff-select"
          className="form-select"
          value={selectedStaffId || ''}
          onChange={(e) => setSelectedStaffId(e.target.value ? parseInt(e.target.value) : null)}
        >
          <option value="">-- Select a Staff --</option>
          {staff.map((s) => (
            <option key={s.id} value={s.id}>{s.name}</option>
          ))}
        </select>
      </div>

      {selectedStaffId && (
        <>
          <h2 className="mt-5">Clock-in/Clock-out Records</h2>
          <div className="mb-3 mt-3">
            <input
              type="text"
              className="form-control"
              placeholder="Cari riwayat waktu..."
              value={clockInOutSearchTerm}
              onChange={(e) => setClockInOutSearchTerm(e.target.value)}
            />
          </div>
          <h3 className="mt-4">Clock-in History</h3>
          <table className="table">
            <thead>
              <tr>
                <th onClick={() => handleClockInOutSort('timestamp')} style={{ cursor: 'pointer' }}>
                  Timestamp {clockInOutSortColumn === 'timestamp' && (clockInOutSortDirection === 'asc' ? '▲' : '▼')}
                </th>
              </tr>
            </thead>
            <tbody>
              {sortedClockInOutRecords.filter(a => a.type === 'clock-in').map((a) => (
                <tr key={a.id}>
                  <td>{new Date(a.timestamp).toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit', second: '2-digit', hour12: false })}</td>
                </tr>
              ))}
            </tbody>
          </table>

          <h3 className="mt-4">Clock-out History</h3>
          <table className="table">
            <thead>
              <tr>
                <th onClick={() => handleClockInOutSort('timestamp')} style={{ cursor: 'pointer' }}>
                  Timestamp {clockInOutSortColumn === 'timestamp' && (clockInOutSortDirection === 'asc' ? '▲' : '▼')}
                </th>
              </tr>
            </thead>
            <tbody>
              {sortedClockInOutRecords.filter(a => a.type === 'clock-out').map((a) => (
                <tr key={a.id}>
                  <td>{new Date(a.timestamp).toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit', second: '2-digit', hour12: false })}</td>
                </tr>
              ))}
            </tbody>
          </table>

          <h2 className="mt-5">Attendance Status Records</h2>
          <div className="mb-3 mt-3">
            <input
              type="text"
              className="form-control"
              placeholder="Cari riwayat status..."
              value={attendanceSearchTerm}
              onChange={(e) => setAttendanceSearchTerm(e.target.value)}
            />
          </div>
          <h3 className="mt-4">Hadir History</h3>
          <table className="table">
            <thead>
              <tr>
                <th onClick={() => handleAttendanceSort('date')} style={{ cursor: 'pointer' }}>
                  Date {attendanceSortColumn === 'date' && (attendanceSortDirection === 'asc' ? '▲' : '▼')}
                </th>
                <th onClick={() => handleAttendanceSort('time')} style={{ cursor: 'pointer' }}>
                  Time {attendanceSortColumn === 'time' && (attendanceSortDirection === 'asc' ? '▲' : '▼')}
                </th>
              </tr>
            </thead>
            <tbody>
              {sortedAttendanceRecords.filter(a => a.status === 'Hadir').map((a) => (
                <tr key={a.id}>
                  <td>{a.date}</td>
                  <td>{a.time}</td>
                </tr>
              ))}
            </tbody>
          </table>

          <h3 className="mt-4">Sakit History</h3>
          <table className="table">
            <thead>
              <tr>
                <th onClick={() => handleAttendanceSort('date')} style={{ cursor: 'pointer' }}>
                  Date {attendanceSortColumn === 'date' && (attendanceSortDirection === 'asc' ? '▲' : '▼')}
                </th>
                <th onClick={() => handleAttendanceSort('time')} style={{ cursor: 'pointer' }}>
                  Time {attendanceSortColumn === 'time' && (attendanceSortDirection === 'asc' ? '▲' : '▼')}
                </th>
              </tr>
            </thead>
            <tbody>
              {sortedAttendanceRecords.filter(a => a.status === 'Sakit').map((a) => (
                <tr key={a.id}>
                  <td>{a.date}</td>
                  <td>{a.time}</td>
                </tr>
              ))}
            </tbody>
          </table>

          <h3 className="mt-4">Izin History</h3>
          <table className="table">
            <thead>
              <tr>
                <th onClick={() => handleAttendanceSort('date')} style={{ cursor: 'pointer' }}>
                  Date {attendanceSortColumn === 'date' && (attendanceSortDirection === 'asc' ? '▲' : '▼')}
                </th>
                <th onClick={() => handleAttendanceSort('time')} style={{ cursor: 'pointer' }}>
                  Time {attendanceSortColumn === 'time' && (attendanceSortDirection === 'asc' ? '▲' : '▼')}
                </th>
              </tr>
            </thead>
            <tbody>
              {sortedAttendanceRecords.filter(a => a.status === 'Izin').map((a) => (
                <tr key={a.id}>
                  <td>{a.date}</td>
                  <td>{a.time}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}
      {!selectedStaffId && <p>Please select a staff member to view their attendance records.</p>}
    </div>
  );
};

export default AdminDashboard;
